        var my_repeater_field_post_id = <?php echo $post->ID; ?>;
        var my_repeater_field_offset = <?php echo $number + 1; ?>;
        var my_repeater_field_nonce = '<?php echo wp_create_nonce('my_repeater_field_nonce'); ?>';
        var my_repeater_ajax_url = '<?php echo admin_url('admin-ajax.php'); ?>';
        var my_repeater_more = true;

        function my_repeater_show_more() {

            jQuery.post(
                my_repeater_ajax_url, {
                    'action': 'my_repeater_show_more',
                    'post_id': my_repeater_field_post_id,
                    'offset': my_repeater_field_offset,
                    'nonce': my_repeater_field_nonce
                },
                function (json) {
                    jQuery('.case_studies_section .case_wrap').append(json['content']);
                    my_repeater_field_offset = json['offset'];
                    if (!json['more']) {
                        jQuery('.load_more_post').css('display', 'none');
                    }
                },
                'json'
            );
        }


        function generateUniqueID() {
            return 'user_' + Date.now() + '_' + Math.random().toString(36).substring(2);
        }


        function handleLinkClick(event) {

            let location = event.currentTarget.href;
            if (!sessionStorage.getItem('user_id')) {

                jQuery('.modal_block').addClass('show_modal');


                document.addEventListener( 'wpcf7mailsent', function( event ) {
                    sessionStorage.setItem('user_id', generateUniqueID());
                    console.log("add new id: " + sessionStorage.getItem('user_id'));
                    jQuery('.modal_block').removeClass('show_modal');

                    window.open(location, '_blank');
                    console.log(location)
                }, false );


            } else {
                window.open(location, '_blank');
                console.log("id exist: " + sessionStorage.getItem('user_id'));
            }


            event.preventDefault();
        }

        function performAdditionalActions() {

        }

